```cpp
#include <string>
#include <vector>
#include <map>
#include <regex>

std::string convertMarkdownToHtml(const std::string& markdown) {
    // Define a map of markdown syntaxes and their corresponding HTML tags
    static const std::map<std::string, std::string> markdown_to_html{
        {"# ", "<h1>"},
        {"## ", "<h2>"},
        {"### ", "<h3>"},
        {"#### ", "<h4>"},
        {"##### ", "<h5>"},
        {"###### ", "<h6>"},
        {"**", "<strong>"},
        {"*", "<em>"},
        {">", "<blockquote>"}
    };

    // Define a regular expression to match markdown headers
    static const std::regex header_regex("#+ ");

    // Define a string builder to hold the converted HTML
    std::ostringstream html;

    // Split the markdown into lines
    std::vector<std::string> lines = split(markdown, "\n");

    // Iterate over each line
    for (const auto& line : lines) {
        // Check if the line is a header
        if (std::regex_match(line, header_regex)) {
            // Extract the level of the header
            size_t level = std::count(line.begin(), line.end(), '#');

            // Add the appropriate HTML tag to the string builder
            html << markdown_to_html.at(line.substr(0, 3 + level));

            // Strip the leading hashtag characters from the line
            line.erase(0, 3 + level);
        }

        // Add the rest of the line as plain text
        html << line;

        // Check if the line contains bold or italic formatting
        if (std::regex_match(line, std::regex("\\*.*\\*"))) {
            html << markdown_to_html.at("**");
            html << regex_replace(line, std::regex("\\*"), "");
            html << markdown_to_html.at("**");
        } else if (std::regex_match(line, std::regex("\\_.*\\_"))) {
            html << markdown_to_html.at("*");
            html << regex_replace(line, std::regex("\\_"), "");
            html << markdown_to_html.at("*");
        }

        // Check if the line contains a blockquote
        if (std::regex_match(line, std::regex(">.*"))) {
            html << markdown_to_html.at(">");
            html << regex_replace(line, std::regex(">"), "");
            html << markdown_to_html.at("</blockquote>");
        }

        // Add a newline to separate lines in the output
        html << "<br>";
    }

    return html.str();
}

// A helper function to split a string by a delimiter character
std::vector<std::string> split(const std::string& s, char delim) {
    std::stringstream ss(s);
    std::string item;
    std::vector<std::string> tokens;
    while (std::getline(ss, item, delim)) {
        tokens.push_back(item);
    }
    return tokens;
}
```

