#include <iostream>
#include <string>
#include <regex>
#include <sstream>

std::string convert_markdown_to_html(std::string& markdown) {
    std::ostringstream html;
    std::regex italic_regex("\\*([^*]+)\\*");
    std::regex bold_regex("__([^_]+)__");
    std::regex link_regex("\\[([^\\]]+)\\]\\(https?://[^\)]+\\)");
    std::regex image_regex("!\\[([^]]+)\\]\\(https?://[^\)]+\\)");

    // Replace italicized text with HTML
    while (std::regex_search(markdown, italic_regex)) {
        std::smatch matches;
        std::regex_search(markdown, matches, italic_regex);
        html << "<em>" << matches[1] << "</em>";
        markdown = matches.suffix().str();
    }

    // Replace bolded text with HTML
    while (std::regex_search(markdown, bold_regex)) {
        std::smatch matches;
        std::regex_search(markdown, matches, bold_regex);
        html << "<strong>" << matches[1] << "</strong>";
        markdown = matches.suffix().str();
    }

    // Replace links with HTML
    while (std::regex_search(markdown, link_regex)) {
        std::smatch matches;
        std::regex_search(markdown, matches, link_regex);
        html << "<a href=\"" << matches[1] << "\">" << matches[1] << "</a>";
        markdown = matches.suffix().str();
    }

    // Replace images with HTML
    while (std::regex_search(markdown, image_regex)) {
        std::smatch matches;
        std::regex_search(markdown, matches, image_regex);
        html << "<img src=\"" << matches[1] << "\" alt=\"" << matches[1] << "\">";
        markdown = matches.suffix().str();
    }

    return html.str();
}

int main() {
    std::string markdown_text = "This is some *markdown-formatted* text with an _italicized_ word and a __bolded__ sentence. Here's a link: [example.com](https://example.com) and an image: ![alt text](https://example.com/image.jpg)";
    std::string html_text = convert_markdown_to_html(markdown_text);
    std::cout << "Markdown: " << markdown_text << "\nHTML: " << html_text << "\n";
}
