vector<string> split(const string& s, char delim) {
  vector<string> tokens;
  string token;
  size_t prev = 0, pos = 0;
  do {
    pos = s.find(delim, prev);
    if (pos == string::npos) pos = s.length();
    token = s.substr(prev, pos - prev);
    if (!token.empty()) tokens.push_back(token);
    prev = pos + 1;
  } while (pos < s.length());
  return tokens;
}

