            html_text << "<code>";
        } else if (c == '```') {
            html_text << "</code>";
        } else {
            html_text << c;
        }
    }

    return html_text.str();
}

int main() {
    std::string markdown = "This is [my link](https://example.com). I love [this](https://www.google.com/) website.";
    std::string html = markdown_to_html(markdown);

    std::cout << "Markdown:\n" << markdown << "\n\nHTML:\n" << html;

    return 0;
}
```

