std::string convert_markdown_to_html(const std::string& markdown) {
    std::stringstream html;
    std::vector<std::string> lines = split(markdown, '\n');

    for (const auto& line : lines) {
        if (line.empty()) {
            html << "<br>";
        } else if (line[0] == '#') {
            html << "<h" << std::count(line.begin(), line.end(), '#') << ">" << line.substr(2) << "</h" << std::count(line.begin(), line.end(), '#') << ">";
        } else if (line[0] == '-') {
            html << "<hr>";
        } else if (line[0] == '>') {
            html << "<blockquote>" << line.substr(2) << "</blockquote>";
        } else if (line[0] == '*') {
            html << "<li>" << line.substr(2) << "</li>";
        } else if (line[0] == '[' && line[line.size() - 1] == ']') {
            const auto link_start = line.find('[');
            const auto link_end = line.find(']');
            const auto link_text = line.substr(link_start + 1, link_end - link_start - 1);
            const auto link_url = line.substr(line.find('(', link_start) + 1, link_end - link_start - 1);
            html << "<a href=\"" << link_url << "\">" << link_text << "</a>";
        } else {
            html << line;
        }
    }

    return html.str();
}

std::vector<std::string> split(const std::string& str, char delim) {
    std::vector<std::string> tokens;
    std::stringstream ss(str);
    std::string item;

    while (std::getline(ss, item, delim)) {
        tokens.push_back(item);
    }

    return tokens;
}
