std::string format_markdown_to_html(std::string& markdown) {
  // Replace markdown headers with HTML headers
  markdown.replace("#", "<h1>");
  markdown.replace("##", "<h2>");
  markdown.replace("###", "<h3>");
  markdown.replace("####", "<h4>");
  markdown.replace("#####", "<h5>");
  markdown.replace("######", "<h6>");

  // Replace markdown links with HTML links
  std::regex link_regex("\\[(.+?)\\]\\((.*?)\\)");
  std::smatch link_match;
  while (std::regex_search(markdown, link_match, link_regex)) {
    std::string link_text = link_match[1];
    std::string link_url = link_match[2];
    markdown.replace(link_match.position(), link_match.length(), 
      "<a href=\"" + link_url + "\">" + link_text + "</a>");
  }

  // Replace markdown images with HTML images
  std::regex image_regex("!\\[(.+?)\\]\\((.*?)\\)");
  while (std::regex_search(markdown, image_match, image_regex)) {
    std::string image_alt = image_match[1];
    std::string image_url = image_match[2];
    markdown.replace(image_match.position(), image_match.length(), 
      "<img src=\"" + image_url + "\" alt=\"" + image_alt + "\">");
  }

  return markdown;
}
```
