    {"#", "<h1>"},
    {"##", "<h2>"},
    {"###", "<h3>"},
    {"####", "<h4>"},
    {"#####", "<h5>"},
    {"######", "<h6>"},
    {"*", "<em>"},
    {"_", "<em>"},
    {"**", "<b>"},
    {"__", "<b>"},
    {"---", "<hr>"},
    {"[", "<a href="},
    {"]", ">"},
    {">>", "</a>"},
    {"`", "<code>"}
  };

  // Initialize an empty string to hold the converted html
  std::string html;

  // Loop through each token in the markdown string
  for (auto& token : tokens) {

    // Check if the current token is a markdown token
    auto iter = markdown_to_html.find(token);

    // If it is, add the corresponding html token to the html string
    if (iter != markdown_to_html.end()) {
