#include <vector>

using namespace std;

vector<int> commonElements(vector<int>& a, vector<int>& b) {
    vector<int> result;
    int idx = 0;

    for (int i = 0; i < a.size(); i++) {
        for (int j = 0; j < b.size(); j++) {
            if (a[i] == b[j]) {
                result[idx++] = a[i];
                break;
            }
        }
    }

    return result;
}

int main() {
    vector<int> a{1, 2, 3, 4};
    vector<int> b{3, 5, 6, 7};

    vector<int> c = commonElements(a, b);

    for (auto e : c) {
        cout << e << " ";
    }
}
