std::string convert_markdown_to_html(const std::string& markdown_text) {
  std::stringstream html_text;

  // Replace links in markdown with HTML links
  auto start_link = markdown_text.find("http");
  while (start_link != std::string::npos) {
    auto end_link = markdown_text.find(" ", start_link);
    if (end_link == std::string::npos) {
      end_link = markdown_text.find("\n", start_link);
    }

    auto link = markdown_text.substr(start_link, end_link - start_link);
    html_text << "<a href=\"" << link << "\">" << link << "</a>";
    start_link = markdown_text.find("http", end_link);
  }

  // Replace bold text in markdown with HTML bold tags
  auto start_bold = markdown_text.find("**");
  while (start_bold != std::string::npos) {
    auto end_bold = markdown_text.find("**", start_bold + 2);
    if (end_bold == std::string::npos) {
      end_bold = markdown_text.find("\n", start_bold + 2);
    }

    auto bold = markdown_text.substr(start_bold + 2, end_bold - start_bold - 2);
    html_text << "<strong>" << bold << "</strong>";
    start_bold = markdown_text.find("**", end_bold);
  }

  // Replace italics text in markdown with HTML italics tags
  auto start_italic = markdown_text.find("*");
  while (start_italic != std::string::npos) {
    auto end_italic = markdown_text.find("*", start_italic + 1);
    if (end_italic == std::string::npos) {
      end_italic = markdown_text.find("\n", start_italic + 1);
    }

    auto italic = markdown_text.substr(start_italic + 1, end_italic - start_italic - 1);
    html_text << "<em>" << italic << "</em>";
    start_italic = markdown_text.find("*", end_italic);
  }

  return html_text.str();
}

int main() {
  std::string markdown_text = "This is an example of [a link](https://example.com). **Bold text** *Italics*";
  std::string html_text = convert_markdown_to_html(markdown_text);

  std::cout << html_text;
}

