```python
def find_longest_common_subsequence(str1, str2):
    # Initialize a matrix to store the results of the dynamic programming
    # The matrix will have the same length as str1 and str2
    dp = [[0] * (len(str1) + 1) for _ in range(len(str2) + 1)]

    # Initialize the first row and column of the matrix with 0s
    for i in range(len(str1) + 1):
        dp[0][i] = 0
    for j in range(len(str2) + 1):
        dp[j][0] = 0

    # Fill in the remaining cells of the matrix using dynamic programming
    for i in range(1, len(str1) + 1):
        for j in range(1, len(str2) + 1):
            if str1[i - 1] == str2[j - 1]:
                dp[j][i] = dp[j - 1][i - 1] + 1
            else:
                dp[j][i] = max(dp[j - 1][i], dp[j][i - 1])

    # Find the longest common subsequence by following the backtrace path
    longest_subseq = []
    i, j = len(str1), len(str2)
    while i > 0 and j > 0:
        if str1[i - 1] == str2[j - 1]:
            longest_subseq.append(str1[i - 1])
            i -= 1
            j -= 1
