```c++
#include <iostream>
using namespace std;

int fib(int n) {
    int f[n+1];
    f[0] = 0;
    f[1] = 1;

    for (int i = 2; i <= n; i++) {
        f[i] = f[i-1] + f[i-2];
    }

    return f[n];
}

int main() {
    int n;
    cout << "Enter a positive integer: ";
    cin >> n;

    if (n <= 0) {
        cout << "Invalid input." << endl;
        return -1;
    }

    cout << "The " << n << "-th Fibonacci number is: " << fib(n) << endl;

    return 0;
}
```

