std::string convertMarkdownToHTML(const std::string& markdownText) {
    std::string htmlText;

    // Replace markdown headers with HTML headers
    std::regex headerRegex("^#+", std::regex_constants::icase);
    std::smatch headerMatch;
    if (std::regex_search(markdownText, headerMatch, headerRegex)) {
        htmlText += "<h" + std::to_string(headerMatch.size()) + ">" + headerMatch.str() + "</h" + std::to_string(headerMatch.size()) + ">";
    }

    // Replace markdown links with HTML links
    std::regex linkRegex("\\[(.*?)\\](\\(.*?\\))", std::regex_constants::icase);
    std::smatch linkMatch;
    while (std::regex_search(markdownText, linkMatch, linkRegex)) {
        htmlText += "<a href=\"" + linkMatch.str(2) + "\">" + linkMatch.str(1) + "</a>";
        markdownText = linkMatch.suffix().str();
    }

    // Replace markdown images with HTML images
    std::regex imageRegex("![.*?](.*?)([\"'])(.*?)\\2", std::regex_constants::icase);
    std::smatch imageMatch;
    while (std::regex_search(markdownText, imageMatch, imageRegex)) {
        htmlText += "<img src=\"" + imageMatch.str(2) + "\" alt=\"" + imageMatch.str(3) + "\">";
        markdownText = imageMatch.suffix().str();
    }

    return htmlText;
}
```

