  // Map of markdown syntax to corresponding HTML syntax
  std::map<std::string, std::string> markdownToHtml = {
    {"#", "<h1>"},
    {"##", "<h2>"},
    {"###", "<h3>"},
    {"####", "<h4>"},
    {"#####", "<h5>"},
    {"######", "<h6>"},
    {"*", "<em>"},
    {"**", "<strong>"},
