  std::smatch match;

  // Replace all markdown syntax with corresponding HTML syntax
  while (std::regex_search(markdown, match, regex)) {
    std::string markdownSyntax = match.str();
    std::string htmlSyntax = markdownToHtml.at(markdownSyntax);
    markdown.replace(markdown.find(markdownSyntax), markdownSyntax.length(), htmlSyntax);
  }

  return markdown;
}

