    std::string word; // Mot à rechercher
    std::string line; // Ligne lue du fichier

    std::cout << "Entrez le mot à rechercher : ";
    std::cin >> word;

    int count = 0; // Nombre d'occurrences du mot

    std::ifstream file("fichier.txt"); // Ouvre le fichier texte
    if (file.is_open()) {
        while (std::getline(file, line)) {
            size_t pos = 0;
            while ((pos = line.find(word, pos)) != std::string::npos) {
                count++;
                pos += word.length();
