std::string md_to_html(const std::string& markdown) {
    // Markdown formázási szabályok listája
    std::regex markdown_regex = std::regex(
        "(?:^|[^#])(#+)([A-Za-z0-9_-]+)(?:$|[^#])", 
        std::regex_constants::extended
    );

