std::string convert_markdown_to_html(std::string input) {
    std::vector<std::string> markdown_tokens = split(input, "\n");
    std::stringstream html_output;
    for (std::string& token : markdown_tokens) {
        if (token == "") {
            html_output << "<br>";
        } else if (token[0] == '#' && token.length() > 1) {
            int number_of_hashes = std::count(token.begin(), token.end(), '#');
            std::string text_to_convert = std::move(token.substr(1));
            for (int i = 0; i < number_of_hashes; i++) {
                html_output << "<h" << number_of_hashes << ">" << text_to_convert << "</h" << number_of_hashes << ">";
            }
        } else if (token[0] == '-' && token.length() > 1) {
            html_output << "<ul>" << text_to_convert << "</ul>";
        } else if (token[0] == '*' && token.length() > 1) {
            html_output << "<li>" << text_to_convert.substr(1, token.length()-2) << "</li>";
        } else {
            html_output << text_to_convert;
        }
    }
    return html_output.str();
}

std::vector<std::string> split(const std::string& input, const char& delimiter) {
    std::stringstream ss(input);
    std::string item;
    std::vector<std::string> tokens;
    while (std::getline(ss, item, delimiter)) {
        tokens.push_back(item);
    }
    return tokens;
}
