from collections import deque

class Queue:
    def __init__(self):
        self.stack1 = deque()
        self.stack2 = deque()

    def push(self, x):
        self.stack1.append(x)
        while self.stack2:
            self.stack1.append(self.stack2.popleft())
        self.stack2 = deque()

    def pop(self):
        while self.stack1:
            self.stack2.append(self.stack1.popleft())
        return self.stack2.popleft()

    def peek(self):
        while self.stack1:
            self.stack2.append(self.stack1.popleft())
        return self.stack2[-1]

    def empty(self):
        return len(self.stack1) == 0 and len(self.stack2) == 0

q = Queue()
q.push(1)
q.push(2)
print(q.peek()) # 1
print(q.pop()) # 1
print(q.empty()) # False

