def mergeSort(arr: Array Int): Array Int := {
    if arr.length < 2 then
        return arr;
    let left := arr[..arr.length / 2];
    let right := arr[arr.length / 2..];
    let sortedLeft := mergeSort(left);
    let sortedRight := mergeSort(right);
    merge(sortedLeft, sortedRight);
}
```

