import time

def record_time(func):
    def wrapper(*args, **kwargs):
        start = time.time()
        result = func(*args, **kwargs)
        end = time.time()
        print(f"{func.__name__} took {end - start} seconds.")
        return result
    return wrapper

@record_time
def slow_function(n):
    time.sleep(n)
    return n

