            replacement = "<p>" + match_str + "</p>";
        } else if (type == "link") {
            replacement = "<a href=\"" + match_str + "\">" + match_str + "</a>";
        } else if (type == "image") {
            replacement = "<img src=\"" + match_str + "\" alt=\"" + match_str + "\">";
        } else if (type == "code") {
            replacement = "<pre><code>" + match_str + "</code></pre>";
        } else if (type == "heading") {
            const auto level = std::stoi(match.str(2));
            replacement = "<h" + std::to_string(level) + ">" + match_str + "</h" + std::to_string(level) + ">";
        } else if (type == "blockquote") {
            replacement = "<blockquote>" + match_str + "</blockquote>";
        } else {
            replacement = "<span>" + match_str + "</span>";
        }

        // Replace the matched text with the HTML
        input.replace(match.position(), match.length(), replacement);
    }

    return output;
}
