```python
class Queue:
    def __init__(self):
        self.items = []
        self.waiting = []

    def enqueue(self, item):
        self.items.append(item)

    def dequeue(self):
        if len(self.waiting) > 0:
            return self.waiting.pop()
        else:
            while len(self.items) > 0:
                self.waiting.append(self.items.pop(0))
            return self.waiting.pop()

    def is_empty(self):
        return len(self.items) == 0 and len(self.waiting) == 0

    def size(self):
        return len(self.items) + len(self.waiting)

queue = Queue()
queue.enqueue("apple")
queue.enqueue("banana")
queue.enqueue("cherry")

print(queue.dequeue()) # banana
print(queue.dequeue()) # cherry
print(queue.is_empty()) # False
print(queue.size()) # 1
