```javascript
const { Client, Intents, SlashCommandBuilder } = require('discord.js');

const client = new Client({ intents: [Intents.FLAGS.GUILDS] });

client.on('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
});

const data = new SlashCommandBuilder()
  .setName('ban')
  .setDescription('Ban a user from the server.')
  .addUserOption((option) =>
    option.setName('user').setDescription('The user to ban.')
  )
  .addStringOption((option) =>
    option
      .setName('reason')
      .setDescription('The reason for the ban.')
      .setRequired(true)
  );

client.application?.commands.create(data);

client.on('interactionCreate', async (interaction) => {
  if (!interaction.isCommand()) return;

  const command = interaction.options.data[0].name;

  if (command === 'ban') {
    const member = interaction.options.getMember('user');
    const reason = interaction.options.getString('reason');

    if (!member.bannable) {
      await interaction.reply({
        content: `I cannot ban ${member.user.tag}.`,
        ephemeral: true,
      });
      return;
    }

    member.ban({ reason });
    await interaction.reply(`Banned ${member.user.tag} for ${reason}`);
  }
});

client.login(process.env.DISCORD_BOT_TOKEN);
```

