#include <iostream>
#include <string>
#include <regex>
using namespace std;

std::string markdown_to_html(std::string& markdown_text) {
    // Определение регулярного выражения для замены
    regex replace_pattern(
        # Заголовки
        "(^#{1,6}) (.*)$"            // Заголовок
        "(\n(#+){1,6} (.*)\n)",       // Подзаголовок
        regex_constants::format_all,
        std::regex_constants::optimize
    );

    smatch matches;

    std::string html_text = markdown_text;
    while (regex_search(html_text, matches, replace_pattern)) {
        // Замена заголовка на HTML-код
        string header_level = matches[1].str();
        int header_level_int = std::stoi(header_level.substr(0, 1));
        string header_text = matches[2].str();

        string html_header = "<h";
        html_header += header_level_int;
        html_header += ">";
        html_header += header_text;
        html_header += "</h" + header_level_int + ">";

        html_text.replace(matches[0].first - markdown_text.begin(), matches[0].length(), html_header);
    }

    return html_text;
}

int main() {
    std::string markdown_text = "## Заголовок\n### Подзаголовок\n* Итог:\n- 1\n- 2";
    std::string html_text = markdown_to_html(markdown_text);
    cout << html_text << endl;

    return 0;
}
```

