#include <string>
#include <boost/algorithm/string.hpp>

std::string markdown_to_html(const std::string& input) {
  std::string output;

  // Sanitize input string
  boost::algorithm::trim(input);
  boost::algorithm::replace_all(input, "\n", "<br/>");
  boost::algorithm::replace_all(input, "##", "<h2>");
  boost::algorithm::replace_all(input, "###", "<h3>");
  boost::algorithm::replace_all(input, "####", "<h4>");

  // Replace markdown code blocks with HTML preformatted text
  size_t start_index = input.find("```");
  while (start_index != std::string::npos) {
    size_t end_index = input.find("```", start_index + 3);
    output += input.substr(start_index + 3, end_index - start_index - 3);
    output += "<pre><code>";
    output += input.substr(start_index + 3, end_index - start_index - 3);
    output += "</code></pre>";
    start_index = end_index;
  }

  // Return formatted HTML string
  return output;
}
```

