#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string word; // Ord att räkna förekomster av
    std::string line; // En rad i textfilen
    int count = 0; // Antal förekomster av ordet

    // Fråga användaren efter ordet att räkna förekomster av
    std::cout << "Ange ordet att räkna förekomster av: ";
    std::cin >> word;

    // Öppna textfilen för läsning
    std::ifstream infile("textfile.txt");

    // Läs rad för rad från filen
    while (std::getline(infile, line)) {
        // Kolla om ordet finns i raden
        if (line.find(word) != std::string::npos) {
            count++;
        }
    }

    // Skriv ut antalet förekomster av ordet
    std::cout << "Antal förekomster av " << word << " är " << count << "." << std::endl;

    return 0;
}

