```c++
#include <iostream>
#include <string>
#include <sstream>
#include <vector>
#include "markdown.h"

std::string to_html(const std::string& markdown) {
    // 创建一个Markdown解析器对象
    Markdown md;

    // 解析Markdown格式文本
    md.parse(markdown);

    // 创建一个HTML字符串
    std::ostringstream html;

    // 逐个遍历Markdown中的元素，并将其转换为HTML
    for (auto& elem : md.elements) {
        switch (elem.type) {
            case Markdown::Heading:
                html << "<h" << elem.level << ">" << elem.text << "</h" << elem.level << ">\n";
                break;
            case Markdown::Paragraph:
                html << "<p>" << elem.text << "</p>\n";
                break;
            case Markdown::Blockquote:
                html << "<blockquote>\n";
                for (auto& p : elem.paragraphs) {
                    html << "<p>" << p.text << "</p>\n";
                }
                html << "</blockquote>\n";
                break;
            case Markdown::Code:
                html << "<pre><code>" << elem.text << "</code></pre>\n";
                break;
            case Markdown::List:
                html << "<ul>\n";
                for (auto& item : elem.items) {
                    html << "<li>" << item.text << "</li>\n";
                }
                html << "</ul>\n";
                break;
            case Markdown::Link:
                html << "<a href=\"" + elem.url + "\">" + elem.text + "</a>";
                break;
            default:
                std::cerr << "Unknown element type: " << static_cast<int>(elem.type) << std::endl;
        }
    }

    return html.str();
}

int main() {
    std::string markdown = "## Hello World\n\nThis is a paragraph.\n\n> This is a blockquote.\n\n```\nHello, World!\n```";
    std::cout << to_html(markdown) << std::endl;

    return 0;
}
```

