std::string convertMarkdownToHTML(const std::string& input) {
  std::stringstream ss;
  std::vector<std::string> tokens;

  // Split the input string into tokens based on whitespace characters
  std::regex whitespace_regex("\\s+");
  std::sregex_token_iterator first{input.begin(), input.end(), whitespace_regex, -1};
  std::sregex_token_iterator last;
  tokens = {first, last};

