  for (const auto& token : tokens) {
    if (token == "``" && !in_code_block) {
      ss << "<pre>";
      in_code_block = true;
    } else if (token == "``" && in_code_block) {
      ss << "</pre>";
      in_code_block = false;
    } else if (token == "_") {
      ss << "<em>";
    } else if (token == "*") {
      ss << "<strong>";
    } else if (token == "[") {
      ss << "<a href=\"";
    } else if (token == "]") {
      ss << "\">";
    } else if (token == "(") {
      ss << "<code>";
    } else if (token == ")") {
      ss << "</code>";
    } else {
      ss << token;
    }
  }

  return ss.str();
}
```

