#include <string>
#include <iostream>
#include <boost/regex.hpp>

std::string convertMarkdownToHtml(const std::string& markdown) {
    // Skriv din regex her
    boost::regex regex("<(.*?)>(.*?)</\\1>");
    boost::smatch match;

    // Søg efter markdown-tekst
    std::string html = "";
    while (regex_search(markdown, match, regex)) {
        std::string tag = match[1];
        std::string content = match[2];

        // Konverter til html-tekst
        if (tag == "h1") {
            html += "<h1>" + content + "</h1>";
        } else if (tag == "h2") {
            html += "<h2>" + content + "</h2>";
        } else if (tag == "em") {
            html += "<em>" + content + "</em>";
        } else if (tag == "strong") {
            html += "<strong>" + content + "</strong>";
        } else if (tag == "code") {
            html += "<code>" + content + "</code>";
        } else if (tag == "blockquote") {
            html += "<blockquote>" + content + "</blockquote>";
        } else if (tag == "list") {
            html += "<ul>" + content + "</ul>";
        } else if (tag == "item") {
            html += "<li>" + content + "</li>";
        }

        // Fjern markdown-teksten
        markdown.replace(match.position(), match.length(), "");
    }

    return html;
}

int main() {
    std::string markdown = "# Hovedoverskrift\n\n## Underoverskrift\n\n### Underunderoverskrift\n\n*Liste*\n\n1. Item 1\n2. Item 2";
    std::string html = convertMarkdownToHtml(markdown);

    std::cout << html << std::endl;

    return 0;
}

