#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // Wort, nach dem gesucht werden soll
    int count = 0; // Anzahl der Vorkommnisse des Wortes

    // Datei öffnen und Wort einlesen
    cout << "Bitte geben Sie das Wort ein, nach dem gesucht werden soll: ";
    cin >> word;
    ifstream file("textfile.txt");
    if (!file.is_open()) {
        cout << "Datei konnte nicht geöffnet werden." << endl;
        return 1;
    }

    // Zeile für Zeile durch die Datei iterieren
    string line;
    while (getline(file, line)) {
        // Wort in der aktuellen Zeile suchen
        size_t pos = line.find(word);
        while (pos != string::npos) {
            count++;
            pos = line.find(word, pos + 1);
        }
    }

    // Ergebnis ausgeben
    cout << "Das Wort '" << word << "' kommt " << count << " Mal in der Datei vor." << endl;
    return 0;
}

