// Import the required libraries
const { Client, GatewayIntentBits } = require('discord.js');
const { SlashCommandBuilder } = require('@discordjs/builders');

// Create a new client object for Discord.js
const client = new Client({ intents: [GatewayIntentBits.Guilds] });

// Define the prefix for the ban command
const prefix = '!';

// Create a new class for the bot
class MyBot {
  constructor() {
    // Create a new command builder for the ban command
    this.commandBuilder = new SlashCommandBuilder()
      .setName('ban')
      .setDescription('Bans a user from the server.');
  }

  // Implement the code for the ban command
  async execute(interaction) {
    // Check if the user has permission to ban other users
    if (!interaction.member.permissions.has('BAN_MEMBERS')) {
      await interaction.reply({
        content: 'You do not have permission to ban other users.',
        ephemeral: true,
      });
      return;
    }

    // Get the user to be banned from the message content
    const userToBan = interaction.options.getUser('user');

    // Ban the user from the server
    await interaction.guild.members.ban(userToBan);

    // Reply to the user with a success message
    await interaction.reply({
      content: `${userToBan.username} has been banned from the server.`,
      ephemeral: true,
    });
  }
}

// Register the ban command for the bot
