#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // Λέξη για την οποία θα μετρήσουμε τις εμφανίσεις
    int count = 0; // Αριθμός εμφανίσεων

    cout << "Δώσε τη λέξη για την οποία θα μετρήσουμε τις εμφανίσεις: ";
    getline(cin, word);

    string line;
    ifstream file("file.txt"); // Άνοιγμα του αρχείου για ανάγνωση
    if (file.is_open()) {
        while (getline(file, line)) {
            size_t pos = 0;
            while ((pos = line.find(word, pos)) != string::npos) {
                count++;
                pos += word.length();
            }
        }
        file.close();
    } else {
        cout << "Error opening file";
        return 1;
    }

    cout << "Ο αριθμός των εμφανίσεων της λέξης " << word << " είναι: " << count;
    return 0;
}

Στο παραπάνω πρόγραμμα, οι ακόλουθες λειτουργίες πραγματοποιούνται:

