from typing import List

def LCS(input1: List[str], input2: List[str]) -> str:
    if len(input1) == 0 or len(input2) == 0:
        return ""

    if input1[0] == input2[0]:
        return input1[0] + LCS(input1[1:], input2[1:])
    else:
        return max(LCS(input1, input2[1:]), LCS(input1[1:], input2), key=len)

