std::string markdown_to_html(std::string& markdown_text) {
    // Regex para encontrar las etiquetas markdown
    std::regex header_regex("^#+");
    std::regex bold_regex("\\*\\w+\\*");
    std::regex italic_regex("\\_\\w+\\_");
    std::regex ordered_list_regex("^[0-9]+\\.)");
    std::regex unordered_list_regex("^[*+-]\\s.*");

