    html_text = std::regex_replace(html_text, italic_regex, "<em>");
    html_text = std::regex_replace(html_text, ordered_list_regex, "<ol><li>");
    html_text = std::regex_replace(html_text, unordered_list_regex, "<ul><li>");

    return html_text;
}
