  markdown_text = std::regex_replace(markdown_text, italic_pattern, "<em>$1</em>");

  std::regex link_pattern("\\[(.*)\\]\\((.*)\\)");
  markdown_text = std::regex_replace(markdown_text, link_pattern, "<a href=\"$2\">$1</a>");

  return markdown_text;
}

