```python
def longest_common_substring(str1, str2):
    # create a 2d array to store the length of the LCS of each substring
    dp = [[0 for _ in range(len(str2) + 1)] for _ in range(len(str1) + 1)]

    # initialize the first row and column of the dp array
    for i in range(1, len(str1) + 1):
        dp[i][0] = 0
    for j in range(1, len(str2) + 1):
        dp[0][j] = 0

    # fill the dp array
    for i in range(1, len(str1) + 1):
        for j in range(1, len(str2) + 1):
            if str1[i - 1] == str2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    # find the longest common substring
    result = ""
    i, j = len(str1), len(str2)
    while i > 0 and j > 0:
        if str1[i - 1] == str2[j - 1] and dp[i][j] == dp[i - 1][j - 1] + 1:
            result = str1[i - 1] + result
            i -= 1
            j -= 1
        elif dp[i][j] == dp[i - 1][j]:
            i -= 1
        else:
            j -= 1

    return result

# test the function with two sample strings
print(longest_common_substring("abcdef", "cdefg")) # should print "cde"
print(longest_common_substring("banana", "abca")) # should print "ab"
```
