}

vector<string> split(const string& s, char delim)
{
    vector<string> elems;
    string item;
    bool inItem = false;

    for (const auto& c : s)
    {
        if (inItem)
        {
            if (c == delim)
            {
                elems.push_back(item);
                inItem = false;
                item.clear();
            }
