const { SlashCommandBuilder } = require("discord.js");

module.exports = {
  data: new SlashCommandBuilder()
    .setName("ban")
    .setDescription("Bans all members who have the given word in their username.")
    .addStringOption((option) =>
      option
        .setName("word")
        .setDescription("The word to check for in usernames.")
        .setRequired(true)
    ),
  async execute(interaction) {
    const word = interaction.options.getString("word");

    await interaction.guild.members.fetch();

    const bannedMembers = await interaction.guild.members
      .filter((member) => member.user.username.includes(word))
      .forEach(async (member) => {
        await member.ban();
      });

    await interaction.reply(
      `Banned ${bannedMembers.size} members with "${word}" in their username.`
    );
  },
};
```

