```c++
#include <iostream>
#include <string>
#include <vector>
#include <sstream>
#include "markdown.h"

std::string convert_markdown_to_html(std::string markdown)
{
    MarkdownParser parser;
    std::vector<MarkdownBlock> blocks = parser.parse(markdown);
    std::stringstream html;

    for (auto& block : blocks) {
        switch (block.type) {
            case MarkdownBlock::Type::Paragraph:
                html << "<p>" << block.content << "</p>";
                break;
            case MarkdownBlock::Type::Header:
                html << "<h" << block.level << ">" << block.content << "</h" << block.level << ">";
                break;
            case MarkdownBlock::Type::CodeBlock:
                html << "<pre><code>" << block.content << "</code></pre>";
                break;
            case MarkdownBlock::Type::List:
                if (block.items.empty()) {
                    html << "<ul><li>None</li></ul>";
                } else {
                    html << "<ul>";
                    for (auto& item : block.items) {
                        html << "<li>" << item.content << "</li>";
                    }
                    html << "</ul>";
                }
                break;
            case MarkdownBlock::Type::Link:
                html << "<a href=\"" << block.destination << "\">" << block.content << "</a>";
                break;
            default:
                break;
        }
    }

    return html.str();
}

int main()
{
    std::string markdown = "## Hello World\n\nThis is a paragraph.\n\n```\nHello World!\n```";
    std::string html = convert_markdown_to_html(markdown);

    std::cout << "Markdown:\n" << markdown << "\n\nHTML:\n" << html;

    return 0;
}
```

