#include <string>
#include <sstream>
#include <vector>
#include <algorithm>
#include <stdexcept>

std::string to_html(const std::string& text) {
    std::stringstream html;
    html << "<div class=\"markdown\">";
    html << text;
    html << "</div>";

    return html.str();
}
