```c#
#include <stdio.h>
#include <stdlib.h>

#define MAX_N 1000

int fib[MAX_N + 1];

int fib_n(int n) {
    if (n == 0 || n == 1) {
        return n;
    }

    if (fib[n] == 0) {
        fib[n] = fib_n(n - 1) + fib_n(n - 2);
    }

    return fib[n];
}

