class Queue:
    def __init__(self):
        self.queue = []
        self.stack = []

    def enqueue(self, item):
        self.queue.append(item)
        while self.stack:
            self.queue.append(self.stack.pop())

    def dequeue(self):
        return self.queue.pop(0)

    def is_empty(self):
        return not self.queue and not self.stack

    def print_queue(self):
        print(self.queue)

q = Queue()
q.enqueue(1)
q.enqueue(2)
q.enqueue(3)
q.print_queue() # [1, 2, 3]
q.dequeue()
q.print_queue() # [2, 3]
```

