#include <SDL.h>
#include <SDL_image.h>

// Khai báo các biến cần thiết
SDL_Window *window;
SDL_Renderer *renderer;
SDL_Surface *surface;
SDL_Texture *texture;
bool running = true;

// Hàm thiết lập SDL
void initSDL() {
    // Thiết lập SDL
    SDL_Init(SDL_INIT_VIDEO);

    // Tạo cửa sổ SDL với kích thước 800x600
    window = SDL_CreateWindow("Snake", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, 800, 600, 0);

    // Tạo renderer
    renderer = SDL_CreateRenderer(window, -1, 0);

    // Tải hình ảnh cho trò chơi
    surface = IMG_Load("snake.png");
    texture = SDL_CreateTextureFromSurface(renderer, surface);
}

// Hàm hiển thị hình ảnh
void showTexture(int x, int y) {
    // Vẽ hình ảnh lên màn hình
    SDL_Rect rect;
    rect.x = x;
    rect.y = y;
    rect.w = surface->w;
    rect.h = surface->h;
    SDL_RenderCopy(renderer, texture, NULL, &rect);
}

// Hàm xử lý sự kiện của chuột
void handleMouseEvent(SDL_Event event) {
    if (event.type == SDL_MOUSEBUTTONDOWN) {
        // Lấy tọa độ của chuột
        int x, y;
        SDL_GetMouseState(&x, &y);

        // Vẽ hình ảnh ở tọa độ của chuột
        showTexture(x, y);
    }
}

// Hàm xử lý sự kiện của bàn phím
void handleKeyboardEvent(SDL_Event event) {
    if (event.type == SDL_KEYDOWN) {
        // Lấy khóa của phím nhấn
        const Uint8 *state = SDL_GetKeyboardState(NULL);
        if (state[SDL_SCANCODE_UP]) {
            // Di chuyển lên
            showTexture(snakeX - 10, snakeY);
            snakeX -= 10;
        } else if (state[SDL_SCANCODE_DOWN]) {
            // Di chuyển xuống
            showTexture(snakeX + 10, snakeY);
            snakeX += 10;
        } else if (state[SDL_SCANCODE_LEFT]) {
            // Di chuyển sang trái
            showTexture(snakeX - 10, snakeY + 10);
            snakeY -= 10;
        } else if (state[SDL_SCANCODE_RIGHT]) {
            // Di chuyển sang phải
            showTexture(snakeX + 10, snakeY + 10);
            snakeY += 10;
        }
    }
}

// Hàm xử lý sự kiện của cửa sổ
void handleWindowEvent(SDL_Event event) {
    if (event.type == SDL_WINDOWEVENT_CLOSE) {
        running = false;
    }
}

// Hàm vòng lặp chính của trò chơi
void mainLoop() {
    while (running) {
        // Nhận sự kiện từ chuột và bàn phím
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            handleMouseEvent(event);
            handleKeyboardEvent(event);
            handleWindowEvent(event);
        }

        // Vẽ lại màn hình với hình ảnh mới nhất của rắn
        showTexture(snakeX, snakeY);

        // Cập nhật màn hình với các thay đổi mới nhất
        SDL_RenderPresent(renderer);

        // Chờ một giây trước khi lặp lại vòng lặp chính tiếp theo
        SDL_Delay(1000);
    }

    // Ngừng sử dụng thư viện SDL và thoát khỏi ứng dụng khi trò chơi kết thúc
    IMG_Quit();
    SDL_Quit();
}

// Hàm khởi động trò chơi và gọi hàm vòng lặp chính khi hoàn thành thiết lập.
int main() {
    initSDL();
    mainLoop();
}

