```
std::string convert_markdown(const std::string& md) {
    boost::locale::generator gen;
    std::locale loc = gen("en_US.UTF-8");
    std::ostringstream html;
    boost::locale::format formatter(html, loc);
    formatter << boost::locale::markdown2html(md);
    return html.str();
}
