      });
    }

    if (target.id === interaction.user.id) {
      return interaction.reply({
        content: "Du kan ikke banne dig selv.",
        ephemeral: true,
      });
    }

    await target.ban();

    return interaction.reply({
      content: "Memberen er blevet banned.",
      ephemeral: true,
    });
  }
});

// Login to Discord with your token
