        for j in range(1, len(str2)+1):
            if str1[i-1] == str2[j-1]:
                dp[i][j] = dp[i-1][j-1] + 1
            else:
                dp[i][j] = max(dp[i-1][j], dp[i][j-1])

    # find the longest common subsequence by tracing back through the dp matrix
    i, j = len(str1), len(str2)
    longest_subsequence = []
    while i > 0 and j > 0:
        if str1[i-1] == str2[j-1]:
