import React, { useState } from 'react';

const messages = [
  { text: 'Hallo, wie geht es dir?', author: 'Bot' },
  { text: 'Gut, danke.', author: 'Benutzer' },
  { text: 'Wie kann ich dir helfen?', author: 'Bot' },
  { text: 'Ich habe ein Problem mit meiner Internetverbindung.', author: 'Benutzer' }
];

function Chatbot() {
  const [chatOpen, setChatOpen] = useState(false);
  const [selectedMessageIndex, setSelectedMessageIndex] = useState(0);

  function toggleChat() {
    setChatOpen(!chatOpen);
  }

  function prevMessage() {
    if (selectedMessageIndex > 0) {
      setSelectedMessageIndex(selectedMessageIndex - 1);
    }
  }

  function nextMessage() {
    if (selectedMessageIndex < messages.length - 1) {
      setSelectedMessageIndex(selectedMessageIndex + 1);
    }
  }

  return (
    <div>
      <button onClick={toggleChat}>
        {chatOpen ? 'Schließen' : 'Öffnen'}
      </button>

      {chatOpen && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Chat</h2>
              <button onClick={toggleChat}>X</button>
            </div>

            <div className="messages">
              {messages.map((message, index) => (
                <div
                  className={`message ${
                    index === selectedMessageIndex ? 'selected' : ''
                  }`}
                  key={index}
                  onClick={() => setSelectedMessageIndex(index)}
                >
                  <span className={message.author === 'Bot' ? 'bot' : ''}>
                    {message.text}
                  </span>
                </div>
              ))}
            </div>

            <div className="buttons">
              <button onClick={prevMessage}>Vorherige Nachricht</button>
              <button onClick={nextMessage}>Nächste Nachricht</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chatbot;
