#include <iostream>
#include <regex>

std::string convert_to_html(const std::string& markdown) {
    std::regex regex("([\\*\\#])([^\\n]+)");
    std::string html;

    for (std::smatch match; std::regex_search(markdown, match, regex);) {
        std::string text = match[2].str();
        std::string type = match[1].str();

        if (type == "*") {
            html += "<strong>" + text + "</strong>";
        } else if (type == "#") {
            html += "<h1>" + text + "</h1>";
        }
    }

    return html;
}

